const Table = ({ postsList, onEdit, editData, onChangeEdit }) => {
  return (
    <table className="table table-striped">
      <thead>
        <tr>
          <th scope="col">Id</th>
          <th scope="col">Title</th>
          <th scope="col"></th>
          <th scope="col"></th>
        </tr>
      </thead>
      <tbody>
        {Array.isArray(postsList) ? (
          <>
            {postsList.map((item) => (
              <tr key={item.id}>
                <th scope="row">{item.id}</th>
                <td>
                  {editData.id === item.id ? (
                    <input
                      type="text"
                      className="form-control"
                      value={item.title}
                      onChange={(e) => onChangeEdit(e.target.value, item.id)}
                    />
                  ) : (
                    item.title
                  )}
                </td>
                <td onClick={() => onEdit(item)}>
                  {editData.id === item.id ? "Save" : "Edit"}
                </td>
                <td>Delete</td>
              </tr>
            ))}
          </>
        ) : null}
      </tbody>
    </table>
  );
};
export default Table;
