import { useEffect, useState } from "react";

const Pagination = ({ count, rowsPerPage, activePage }) => {
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    if (count) {
      let pages = Math.ceil(count / rowsPerPage);
      setTotalPages(pages);
    }
  }, [count, rowsPerPage]);

  const getPages = () => {
    let arr = Array.from(Array(totalPages).keys());
    if (activePage === 0) {
      arr = arr.slice(activePage, activePage + 3);
    } else if (activePage === totalPages.length - 1) {
      arr = arr.slice(activePage - 3, activePage);
    } else {
      arr = arr.slice(activePage - 1, activePage + 1);
    }
    return arr;
  };

  return (
    <nav>
      <ul className="pagination">
        <li className={`page-item ${activePage === 1 ? "disabled" : ""}`}>
          <span className="page-link">Previous</span>
        </li>
        {getPages().map((item) => (
          <li className={`page-item ${activePage === item ? "active" : ""} `}>
            <span className="page-link">{item + 1}</span>
          </li>
        ))}
        <li className="page-item">
          <span className="page-link">Next</span>
        </li>
      </ul>
    </nav>
  );
};
export default Pagination;
