const Table = ({ postsList, onEdit, editData }) => {
  return (
    <table className="table table-striped">
      <thead>
        <tr>
          <th scope="col">Id</th>
          <th scope="col">Title</th>
          <th scope="col"></th>
          <th scope="col"></th>
        </tr>
      </thead>
      <tbody>
        {Array.isArray(postsList) ? (
          <>
            {postsList.map((item) => (
              <tr key={item}>
                <th scope="row">{item.id}</th>
                <td>{editData === item.id ? <input /> : item.title}</td>
                <td onClick={() => onEdit(item)}>Edit</td>
                <td>Delete</td>
              </tr>
            ))}
          </>
        ) : null}
      </tbody>
    </table>
  );
};
export default Table;
